import React, { Component } from 'react'
import { Card, Button, Table, Space } from 'antd'
import {
    PlusOutlined
} from '@ant-design/icons';
import LinkButton from '../../components/LinkButton';

export default class PlatForm extends Component {

    state = { visible: 0 };

    // 打开添加抽屉
    addUser = () => {
        this.setState({ visible: 1 })
    }

    // 关闭抽屉
    onClose = () => {
        this.setState({ visible: 0 })
    }

    render() {

        const columns = [
            {
                title: '手机号',
                dataIndex: 'phone',
                key: '1',
                align: 'center',
            },
            {
                title: '姓名',
                dataIndex: 'name',
                key: '2',
                align: 'center',
            },
            {
                title: '状态',
                dataIndex: 'state',
                key: '3',
                align: 'center',
            },
            {
                title: '操作',
                key: '4',
                render: () => (
                    <Space size="middle">
                        <LinkButton>编辑</LinkButton>
                    </Space>
                ),
                align: 'center'
            },
        ];

        const dataSource = [
            {
                key: '1',
                phone: '123456',
                name: '李四',
                state: '启用',
            },
        ]
        return (
            <div>
                <div>
                    <Card
                        title={<span style={{ fontSize: '30px' }}>平台用户</span>}
                        extra={
                            <Button icon={<PlusOutlined />} type="primary" onClick={this.addUser}>新增</Button>
                        }
                    >
                        <Table
                            columns={columns}
                            dataSource={dataSource}
                            bordered
                            rowKey="key"
                            pagination={false}
                        />
                    </Card>
                </div>
                {/* 新增的抽屉 */}
                <div>
                    <Drawer
                        title="新增用户"
                        width={720}
                        onClose={this.onClose}
                        visible={visible === 1}
                        bodyStyle={{ paddingBottom: 80 }}
                    >

                    </Drawer>

                </div>
            </div>
        )
    }
}
