import { Card, Divider, Table, Checkbox, Row, Col, Select } from 'antd'
import React, { Component } from 'react'
import UserHead from '../../../components/UserHead';
import { createFromIconfontCN } from '@ant-design/icons';
import './OrderTicket.css'

const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_2779508_qg92a09n5ub.js',
})

const { Option } = Select
export default class OrderTicket extends Component {

    state = {
        passengers: [
            { id: 1, name: '张三', age: 18 },
            { id: 2, name: '李四', age: 20 },
        ],
        dataSource: [
            {
                key: '1',
                order: 1,
                level: '豪华',
                name: '张三',
                idCard: '510811111111',
                phone: '182222',
            },
            {
                key: '2',
                order: 2,
                name: '张三',
                idCard: '510811111111',
                phone: '182222',
            },
        ]
    }

    // 选中乘客
    checkPassenger = (checkValus) => {
        const { passengers } = this.state
        const newArr = passengers.filter((passenger) => {
            return checkValus.includes(passenger.id)
        })

    }
    deletePassenger = (key) => {
        const dataSource = [...this.state.dataSource];
        console.log(dataSource)
        this.setState({ dataSource: dataSource.filter(item => item.key !== key) })
    }

    render() {

        const { dataSource } = this.state

        const columns = [
            {
                title: '序号',
                dataIndex: 'order',
                key: '1',
                align: 'center',
            },
            {
                title: '席别',
                dataIndex: 'level',
                key: '2',
                align: 'center',
                render: () => (
                    <Select style={{ width: 100 }} >
                        <Option value={1}>豪华</Option>
                        <Option value={2}>普通</Option>
                    </Select>
                ),

            },
            {
                title: '姓名',
                dataIndex: 'name',
                key: '3',
                align: 'center',
            },
            {
                title: '证件号码',
                key: '4',
                dataIndex: 'idCard',
                align: 'center',
            },
            {
                title: '手机号码',
                key: '5',
                dataIndex: 'phone',
                align: 'center',
            },
            {
                title: '操作',
                key: '7',
                render: () => (
                    <IconFont type="anticonicon--shanchu" onClick={this.deletePassenger} />
                ),
                align: 'center'
            },
        ];
        return (
            <div>
                <UserHead />
                <div className="order-trian-info">
                    <Card
                        title="列车信息"
                        headStyle={{ backgroundColor: '#3B99FC' }}
                        bodyStyle={{ backgroundColor: '#EEF1F8' }}
                    >
                        <p className="order-train-info-p">
                            <strong className="order-train-info-mr5">2021-07-04</strong>
                            <strong className="order-train-info-ml5">G2121</strong>
                            次
                            <strong className="order-train-info-ml5">成都东</strong>
                            站
                            <strong className="order-train-info-s">（17:35开）——北京</strong>
                            站（19:35到）
                        </p>
                        <p>
                            <span>豪华（
                                <span style={{ color: '#fb7403' }}>￥500</span>
                                ）6张
                            </span>
                            <span style={{ marginLeft: '50px' }}>普通（
                                <span style={{ color: '#fb7403' }}>￥500</span>
                                ）6张
                            </span>
                            <span style={{ marginLeft: '50px' }}>经济（
                                <span style={{ color: '#fb7403' }}>￥500</span>
                                ）6张
                            </span>
                        </p>
                    </Card>
                </div>
                <div className="order-passenger-info">
                    <Card
                        title="乘客信息"
                        headStyle={{ backgroundColor: '#3B99FC' }}
                        bodyStyle={{ backgroundColor: '#FFFFFF' }}
                    >
                        <div>
                            <span>
                                <IconFont type="anticonchengcheren" />
                                <span style={{ marginLeft: '10px' }}>乘车人</span>
                            </span>
                        </div>
                        <div>
                            <Checkbox.Group style={{ width: '100%', marginLeft: '25px' }} onChange={this.checkPassenger} >
                                <Row>
                                    <Col span={2}>
                                        <Checkbox value={1}>张三</Checkbox>
                                    </Col>
                                    <Col span={2}>
                                        <Checkbox value={2}>李四</Checkbox>
                                    </Col>
                                </Row>
                            </Checkbox.Group>
                        </div>
                        <Divider dashed />
                        <Table
                            columns={columns}
                            dataSource={dataSource}
                            bordered
                            rowKey="key"
                            pagination={false}
                        />
                    </Card>
                </div>
            </div>
        )
    }
}
