import React, { Component } from 'react'
import {
    PageHeader, Input, List, Card, Pagination,
    Drawer, Form, Button, Col, Row, Space
} from 'antd';
import { Link } from 'react-router-dom'
import './site.css'

const { Search } = Input
const data = [
    {
        title: 'Title 1',
    },
    {
        title: 'Title 2',
    },
    {
        title: 'Title 3',
    },
    {
        title: 'Title 4',
    },
];

export default class Site extends Component {

    state = { visible: false }

    onSearch = () => {
        console.log("search")
    }
    // 打开抽屉
    showDrawer = () => {
        this.setState({
            visible: true,
        });
    }
    // 关闭抽屉
    onClose = () => {
        this.setState({
            visible: false,
        });
    }

    render() {
        return (
            <div>
                <div>
                    <PageHeader
                        title={<span style={{ fontSize: '30px' }}>站点管理</span>}
                        subTitle={<Button onClick={this.showDrawer} style={{ marginLeft: '20px' }}>新增</Button>}
                    />
                    <Search style={{ width: '300px' }} placeholder="名称、首字母、简拼" onSearch={this.onSearch} enterButton />
                </div>
                <br />
                <br />
                <div>
                    <List
                        className="site-list"
                        grid={{ gutter: 16, column: 4 }}
                        dataSource={data}
                        renderItem={item => (
                            <List.Item>
                                <Card style={{ textAlign: 'center' }}>成都</Card>
                            </List.Item>
                        )}
                    />
                </div>
                <div>
                    <Pagination className="site-pagination" defaultCurrent={1} total={500} />
                </div>
                {/* 新增的抽屉 */}
                <div>
                    <Drawer
                        title="新建站点"
                        width={600}
                        onClose={this.onClose}
                        visible={this.state.visible}
                        bodyStyle={{ paddingBottom: 80 }}
                        footer={
                            <div
                                style={{
                                    textAlign: 'right',
                                }}
                            >
                                <Space size="500px">
                                    <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button onClick={this.onClose} type="primary">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        }
                    >
                        <Form layout="vertical" hideRequiredMark>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="siteName"
                                        label="站点名称"
                                        rules={[{ required: true, message: '请输入站点名称' }]}
                                    >
                                        <Input placeholder="请输入站点名称" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="initials"
                                        label="首字母"
                                        rules={[{ required: true, message: '请输入首字母' }]}
                                    >
                                        <Input placeholder="请输入首字母" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="abbreviation"
                                        label="简拼"
                                        rules={[{ required: true, message: '请输入简拼' }]}
                                    >
                                        <Input placeholder="请输入简拼" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="longitude"
                                        label="经度"
                                        rules={[{ required: true, message: '请输入经度' }]}
                                    >
                                        <Input placeholder="请输入经度" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="latitude"
                                        label="纬度"
                                        rules={[{ required: true, message: '请输入纬度' }]}
                                    >
                                        <Input placeholder="请输入纬度" />
                                    </Form.Item>
                                </Col>
                            </Row>
                        </Form>
                    </Drawer>
                </div>
            </div>
        )
    }
}
