import React, { Component } from 'react'
import {
    Input, Card, Table,
    Drawer, Form, Button, Col, Row, Space, message, Radio
} from 'antd';
import {
    PlusOutlined
} from '@ant-design/icons';
import LinkButton from '../../../components/LinkButton';
import { adminList } from '../../../api/dev/admin';

export default class PlatForm extends Component {
    myForm = React.createRef()
    state = {
        visible: 0,
        pageNum: 1,
        pageSize: 5,
        dataSource: []
    }

    async componentDidMount() {
        const { pageNum, pageSize } = this.state
        const result = await adminList(pageNum, pageSize)
        if (result.code === 1) {
            const newData = result.data.map((item) => {
                item.state = item.state === 1 ? '启用' : '禁用'
                return item
            })
            this.setState({ dataSource: newData })
        } else {
            message.error(result.msg)
        }
    }

    // 打开添加抽屉
    showAddUser = () => {
        this.setState({ visible: 1 })
    }

    // 关闭抽屉
    onClose = () => {
        this.setState({ visible: 0 })
        this.myForm.current.resetFields()
    }

    // 添加用户
    addUser = (values) => {
        console.log(values)
        // message.success('添加成功')
        // this.setState({ visible: 0 })
        // this.myForm.current.resetFields()
    }

    // 打开编辑的抽屉
    showUpdateUser = () => {
        this.setState({ visible: 2 })
    }

    updateUser = () => {
        message.success('编辑成功')
        this.setState({ visible: 0 })
        this.myForm.current.resetFields()
    }

    render() {
        const { visible, dataSource } = this.state
        const columns = [
            {
                title: '手机号',
                dataIndex: 'phone',
                key: '1',
                align: 'center',
            },
            {
                title: '姓名',
                dataIndex: 'name',
                key: '2',
                align: 'center',
            },
            {
                title: '状态',
                dataIndex: 'state',
                key: '3',
                align: 'center',
            },
            {
                title: '操作',
                key: '4',
                render: () => (
                    <Space size="middle">
                        <LinkButton onClick={this.showUpdateUser}>编辑</LinkButton>
                    </Space>
                ),
                align: 'center'
            },
        ];

        return (
            <div>
                <div>
                    <Card
                        title={<span style={{ fontSize: '30px' }}>平台用户</span>}
                        extra={
                            <Button icon={<PlusOutlined />} type="primary" onClick={this.showAddUser}>新增</Button>
                        }
                    >
                        <Table
                            columns={columns}
                            dataSource={dataSource}
                            bordered
                            rowKey="id"
                            pagination={false}
                        />
                    </Card>
                </div>
                {/* 新增的抽屉 */}
                <div>
                    <Drawer
                        title="新增用户"
                        width={500}
                        onClose={this.onClose}
                        visible={visible === 1}
                        bodyStyle={{ paddingBottom: 80 }}
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.addUser}
                            ref={this.myForm}
                        >
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="phone"
                                        label="手机号码"
                                        rules={[{ required: true, message: '请输入手机号码!' }]}
                                        required="true"
                                    >
                                        <Input autoComplete="off" placeholder="请输入手机号码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="name"
                                        label="姓名"
                                        rules={[{ required: true, message: '请输入姓名!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入姓名" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="password"
                                        label="密码"
                                        rules={[{ required: true, message: '请输入密码!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入密码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="state"
                                        label="状态"
                                        rules={[{ required: true, message: '请选择状态！' }]}
                                    >
                                        <Radio.Group name="state" >
                                            <Radio value={1}>启用</Radio>
                                            <Radio value={2}>禁用</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Drawer>
                </div>
                {/* 编辑的抽屉 */}
                <div>
                    <Drawer
                        title="编辑用户"
                        width={500}
                        onClose={this.onClose}
                        visible={visible === 2}
                        bodyStyle={{ paddingBottom: 80 }}
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.updateUser}
                            ref={this.myForm}
                        >
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="phone"
                                        label="手机号码"
                                        rules={[{ required: true, message: '请输入手机号码!' }]}
                                        required="true"
                                    >
                                        <Input autoComplete="off" placeholder="请输入手机号码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="name"
                                        label="姓名"
                                        rules={[{ required: true, message: '请输入姓名!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入姓名" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="password"
                                        label="密码"
                                        rules={[{ required: true, message: '请输入密码!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入密码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="state"
                                        label="状态"
                                        rules={[{ required: true, message: '请选择状态！' }]}
                                    >
                                        <Radio.Group name="state" >
                                            <Radio value={1}>启用</Radio>
                                            <Radio value={2}>禁用</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Drawer>
                </div>
            </div>
        )
    }
}
