import React, { Component } from 'react'
import {
    PageHeader, Input, List, Card, Pagination,
    Drawer, Form, Button, Col, Row
} from 'antd';
import { Link } from 'react-router-dom'
import './site.css'

const { Search } = Input
const data = [
    {
        title: 'Title 1',
    },
    {
        title: 'Title 2',
    },
    {
        title: 'Title 3',
    },
    {
        title: 'Title 4',
    },
];

export default class Site extends Component {

    onSearch = () => {
        console.log("search")
    }

    // onSearch = value => console.log(value);

    // 打开抽屉
    showDrawer = () => {
        this.setState({
            visible: true,
        });
    }

    // 关闭抽屉
    onClose = () => {
        this.setState({
            visible: false,
        });
    }

    render() {
        return (
            <div>
                <div>
                    <PageHeader
                        title={<span style={{ fontSize: '30px' }}>站点管理</span>}
                        subTitle={<Button onClick={this.showDrawer} style={{ marginLeft: '20px' }}>新增</Button>}
                    />
                    <Search style={{ width: '300px' }} placeholder="名称、首字母、简拼" onSearch={this.onSearch} enterButton />
                </div>
                <br />
                <br />
                <div>
                    <List
                        className="site-list"
                        grid={{ gutter: 16, column: 4 }}
                        dataSource={data}
                        renderItem={item => (
                            <List.Item>
                                <Card style={{ textAlign: 'center' }}>成都</Card>
                            </List.Item>
                        )}
                    />
                </div>
                <div>
                    <Pagination className="site-pagination" defaultCurrent={1} total={500} />
                </div>
                {/* 新增的抽屉 */}
                <div>
                    <Drawer
                        title="Create a new account"
                        width={720}
                        onClose={this.onClose}
                        visible={this.state.visible}
                        bodyStyle={{ paddingBottom: 80 }}
                        footer={
                            <div
                                style={{
                                    textAlign: 'right',
                                }}
                            >
                                <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                    Cancel
                                </Button>
                                <Button onClick={this.onClose} type="primary">
                                    Submit
                                </Button>
                            </div>
                        }
                    >
                        <Form layout="vertical" hideRequiredMark>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="name"
                                        label="Name"
                                        rules={[{ required: true, message: 'Please enter user name' }]}
                                    >
                                        <Input placeholder="Please enter user name" />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item
                                        name="url"
                                        label="Url"
                                        rules={[{ required: true, message: 'Please enter url' }]}
                                    >
                                        <Input
                                            style={{ width: '100%' }}
                                            addonBefore="http://"
                                            addonAfter=".com"
                                            placeholder="Please enter url"
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="owner"
                                        label="Owner"
                                        rules={[{ required: true, message: 'Please select an owner' }]}
                                    >
                                        <Select placeholder="Please select an owner">
                                            <Option value="xiao">Xiaoxiao Fu</Option>
                                            <Option value="mao">Maomao Zhou</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item
                                        name="type"
                                        label="Type"
                                        rules={[{ required: true, message: 'Please choose the type' }]}
                                    >
                                        <Select placeholder="Please choose the type">
                                            <Option value="private">Private</Option>
                                            <Option value="public">Public</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="approver"
                                        label="Approver"
                                        rules={[{ required: true, message: 'Please choose the approver' }]}
                                    >
                                        <Select placeholder="Please choose the approver">
                                            <Option value="jack">Jack Ma</Option>
                                            <Option value="tom">Tom Liu</Option>
                                        </Select>
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item
                                        name="dateTime"
                                        label="DateTime"
                                        rules={[{ required: true, message: 'Please choose the dateTime' }]}
                                    >
                                        <DatePicker.RangePicker
                                            style={{ width: '100%' }}
                                            getPopupContainer={trigger => trigger.parentElement}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={24}>
                                    <Form.Item
                                        name="description"
                                        label="Description"
                                        rules={[
                                            {
                                                required: true,
                                                message: 'please enter url description',
                                            },
                                        ]}
                                    >
                                        <Input.TextArea rows={4} placeholder="please enter url description" />
                                    </Form.Item>
                                </Col>
                            </Row>
                        </Form>
                    </Drawer>
                </div>
            </div>
        )
    }
}
