import { Card, Table, Form, message, DatePicker, Button, Checkbox, Row, Col, Select } from 'antd'
import React, { Component } from 'react'
import moment from 'moment'
import UserHead from '../../../components/UserHead'
import LinkButton from '../../../components/LinkButton'
import MyNavLink from '../../../components/MyNavLink';
import './TicketList.css'

import { getSiteList } from '../../../api/dev/train';
import { moreTicket } from '../../../api/user/search';

const { Option } = Select
export default class TicketList extends Component {

    state = {
        dataSource: [],
        siteList: []
    }

    // 跳转到预定页面
    orderTicket = () => {
        console.log('预定')
    }

    // 条件搜索
    searchTicket = (values) => {
        console.log(values)
    }

    // 设置不能选择的日期
    disabledDate = (current) => {
        return current && current < moment().subtract(1, 'days')
    }
    async componentDidMount() {
        this.setState({ dataSource: this.props.location.state })
        const result = await getSiteList()
        if (result.code === 1) {
            this.setState({ siteList: result.data })
        } else {
            message.error(result.msg)
        }
    }

    render() {
        const { dataSource, siteList } = this.state
        const columns = [
            {
                title: '车次',
                dataIndex: 'trainNumber',
                key: '1',
                align: 'center',
            },
            {
                title: '起点站',
                dataIndex: 'startName',
                key: '2',
                align: 'center',
            },
            {
                title: '终点站',
                dataIndex: 'endName',
                key: '3',
                align: 'center',
            },
            {
                title: '出发时间',
                key: '4',
                dataIndex: 'startTime',
                align: 'center',
            },
            {
                title: '到达时间',
                key: '5',
                dataIndex: 'endTime',
                align: 'center',
            },
            {
                title: '历时',
                key: '6',
                dataIndex: 'timeConsume',
                align: 'center',
            },
            ,
            {
                title: '豪华型',
                key: '7',
                dataIndex: 'luxury',
                align: 'center',
                render: (record) => {
                    if (!record) {
                        return "-"
                    } else {
                        return record
                    }
                }
            },
            ,
            {
                title: '普通',
                key: '8',
                dataIndex: 'ordinal',
                align: 'center',
                render: (record) => {
                    if (!record) {
                        return "-"
                    } else {
                        return record
                    }
                }
            },
            ,
            {
                title: '经济',
                key: '9',
                dataIndex: 'econ',
                align: 'center',
                render: (record) => {
                    if (!record) {
                        return "-"
                    } else {
                        return record
                    }
                }
            },
            {
                title: '操作',
                key: '7',
                render: () => (
                    <MyNavLink to="/user/orderTicket">
                        <LinkButton >预定</LinkButton>
                    </MyNavLink>
                ),
                align: 'center'
            },
        ]

        return (
            <div>
                <UserHead />
                <div className="search-ticket">
                    <Form name="search-ticket" layout="inline" onFinish={this.searchTicket}>
                        <Form.Item
                            name="startStation"
                        >
                            <Select
                                showSearch
                                placeholder="可以搜索站点"
                                filterOption={(input, option) =>
                                    option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                }
                            >
                                {siteList.map((item, _) => {
                                    return <Option key={item.id} value={item.id}>{item.siteName}</Option>
                                })}
                            </Select>
                        </Form.Item>
                        <Form.Item
                            name="endStation"
                        >
                            <Select
                                showSearch
                                placeholder="可以搜索站点"
                                filterOption={(input, option) =>
                                    option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                }
                            >
                                {siteList.map((item, _) => {
                                    return <Option key={item.id} value={item.id}>{item.siteName}</Option>
                                })}
                            </Select>
                        </Form.Item>
                        <Form.Item
                            name="startDate"
                        >
                            <DatePicker
                                format="YYYY-MM-DD"
                                disabledDate={this.disabledDate}
                            />
                        </Form.Item>
                        <Form.Item
                            name="types"
                            style={{ width: '150px' }}>

                            <Checkbox.Group style={{ width: '100%' }} onChange={this.checkTrainType}>
                                <Row>
                                    <Col span={12}>
                                        <Checkbox value={1}>动车D</Checkbox>
                                    </Col>
                                    <Col span={12}>
                                        <Checkbox value={2}>高铁G</Checkbox>
                                    </Col>
                                </Row>
                            </Checkbox.Group>
                        </Form.Item>
                        <Form.Item >
                            <Button
                                shape="round"
                                style={{ width: '100px', marginLeft: '50px' }}
                                type="primary"
                                htmlType="submit"
                            >
                                查询
                            </Button>
                        </Form.Item>
                    </Form>
                </div>

                <Card
                    bordered={false}
                >
                    <Table
                        columns={columns}
                        dataSource={dataSource}
                        bordered
                        rowKey="id"
                    // pagination={{
                    //     onChange: page => {
                    //         this.getAdminList(page, pageSize)
                    //     },
                    //     pageSize: pageSize,
                    //     total: totalCount,
                    // }}
                    />
                </Card>
            </div>
        )
    }
}
