import React, { Component } from 'react'
import { Card, Button, Table, Space, message } from 'antd'
import {
    PlusOutlined
} from '@ant-design/icons';
import LinkButton from '../../../../components/LinkButton';
import MyNavLink from '../../../../components/MyNavLink'
import { getTrainList } from '../../../../api/dev/train';

export default class TrainsHome extends Component {

    state = {
        trainList: []
    }

    async componentDidMount() {
        // 拉取列表数据
        const result = await getTrainList(1, 5)
        if (result.code === 1) {
            const newData = result.data.map((item, key) => {
                item.key = item.id
                item.type = item.type === 1 ? '动车' : '高铁'
                item.trainStatus = item.trainStatus === 0 ? '禁用' : '启用'
                item.isReturn = item.isReturn === 0 ? '否' : '是'
                return item
            })
            this.setState({ trainList: newData })
        } else {
            message.error(result.msg)
        }
    }

    render() {
        const { trainList } = this.state
        const columns = [
            {
                title: '车次',
                dataIndex: 'trainNumber',
                key: '1',
                align: 'center',
            },
            {
                title: '类别',
                dataIndex: 'type',
                key: '2',
                align: 'center',
            },
            {
                title: '起点',
                dataIndex: 'startName',
                key: '3',
                align: 'center',
            },
            {
                title: '终点',
                key: '4',
                dataIndex: 'endName',
                align: 'center',
            },
            {
                title: '发车时间',
                key: '5',
                dataIndex: 'startTime',
                align: 'center',
            },
            {
                title: '耗时',
                key: '6',
                dataIndex: 'timeConsume',
                align: 'center',
            },
            {
                title: '车厢',
                key: '7',
                dataIndex: 'carriage',
                align: 'center',
            },
            {
                title: '状态',
                key: '8',
                dataIndex: 'trainStatus',
                align: 'center',
            },
            {
                title: '操作',
                key: '9',
                render: (train) => (
                    <Space size="middle">
                        {/* <MyNavLink to="/dev/home/trains/addupdate"> */}
                        <LinkButton onClick={() => { this.props.history.push('/dev/home/trains/addupdate', { train }) }}>编辑</LinkButton>
                        {/* </MyNavLink> */}
                    </Space>
                ),
                align: 'center'
            },
        ]
        return (
            <div >
                <div>
                    <Card
                        title={<span style={{ fontSize: '30px' }}>车次管理</span>}
                        extra={
                            <MyNavLink to="/dev/home/trains/addupdate">
                                <Button icon={<PlusOutlined />} type="primary" >新增</Button>
                            </MyNavLink>
                        }
                    >
                        <Table
                            columns={columns}
                            dataSource={trainList}
                            bordered
                            rowKey="key"
                        />
                    </Card>
                </div>
            </div >
        )
    }
}
