import { Card, Divider, Table, Checkbox, Row, Col, Select, Popconfirm, Button, Layout, Modal } from 'antd'
import React, { Component } from 'react'
import UserHead from '../../../components/UserHead';
import { createFromIconfontCN } from '@ant-design/icons';
import './OrderTicket.css'

const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_2779508_d3ob7o1ykpv.js',
})

const { Option } = Select
const { Header, Content, Footer } = Layout
export default class OrderTicket extends Component {

    state = {
        dataSource: [
            // {
            //     key: 1,
            //     order: 1,
            //     level: 1,
            //     name: '张三',
            //     idCard: '510811111111',
            //     phone: '182222',
            // },
            // {
            //     key: 2,
            //     order: 2,
            //     level: 2,
            //     name: '张三',
            //     idCard: '510811111111',
            //     phone: '182222',
            // },
        ],
        order: 1,
        visible: false,
        trainInfo: {}
    }

    // 选中乘客
    checkPassenger = (checkValus) => {
        // const { order, dataSource } = this.state
        // const newData = {
        //     key: order,
        //     order: order,
        //     level: 1,
        //     name: '张三',
        //     idCard: '56565',
        //     phone: '18222',
        // }
        // this.setState({
        //     dataSource: [...dataSource, newData],
        //     order: order + 1
        // })

    }
    // 删除乘客
    deletePassenger = (key) => {
        const dataSource = [...this.state.dataSource]
        const newDataSource = dataSource.filter((item) => {
            if (item.key !== key) {
                if (item.key > key) {
                    --item.key
                    --item.order
                }
                return item
            }
        })
        if (newDataSource.length === 0) this.setState({ order: 1 })
        this.setState({
            dataSource: newDataSource,
            checked: false
        })
    }

    // 处理选中乘客
    handleCheck = (event) => {
        const { dataSource, order } = this.state
        const { checked, value } = event.target
        if (checked) {
            const newData = {
                key: order,
                order: order,
                level: 1,
                name: '张三',
                idCard: '56565',
                phone: '18222',
            }
            this.setState({
                dataSource: [...dataSource, newData],
                order: order + 1,
                checked: true
            })
        } else {
            this.deletePassenger(value)
        }
    }

    // 处理对话框完成
    handleOk = () => {
        this.setState({ visible: false })
        this.props.history.push({ pathname: '/user/orderInfo' })
    }

    // 关闭对话框
    handleCancel = () => {
        this.setState({ visible: false })
    }

    // 打开订单详情确认对话框
    showOrderInfo = () => {
        this.setState({ visible: true })
    }

    componentDidMount() {
        this.setState({ trainInfo: this.props.location.state })
    }

    render() {

        const { dataSource, visible, trainInfo } = this.state
        const columns = [
            {
                title: '序号',
                dataIndex: 'order',
                key: '1',
                align: 'center',
            },
            {
                title: '席别',
                dataIndex: 'level',
                key: '2',
                align: 'center',
                render: () => (
                    <Select style={{ width: 100 }} defaultValue={1} >
                        <Option value={1}>豪华</Option>
                        <Option value={2}>普通</Option>
                    </Select>
                ),

            },
            {
                title: '姓名',
                dataIndex: 'name',
                key: '3',
                align: 'center',
            },
            {
                title: '证件号码',
                key: '4',
                dataIndex: 'idCard',
                align: 'center',
            },
            {
                title: '手机号码',
                key: '5',
                dataIndex: 'phone',
                align: 'center',
            },
            {
                title: '操作',
                key: '7',
                render: (_, record) =>
                    this.state.dataSource.length >= 1 ? (
                        <Popconfirm title="确定删除吗？" onConfirm={() => this.deletePassenger(record.key)}>
                            <IconFont type="anticonicon--shanchu" />
                        </Popconfirm>
                    ) : null,
                align: 'center'
            },
        ];

        const columnsInfo = [
            {
                title: '序号',
                dataIndex: 'order',
                key: '1',
                align: 'center',
            },
            {
                title: '席别',
                dataIndex: 'level',
                key: '2',
                align: 'center',
            },
            {
                title: '姓名',
                dataIndex: 'name',
                key: '3',
                align: 'center',
            },
            {
                title: '证件号码',
                key: '4',
                dataIndex: 'idCard',
                align: 'center',
            },
            {
                title: '手机号码',
                key: '5',
                dataIndex: 'phone',
                align: 'center',
            },
        ];

        return (
            <div>
                <UserHead />
                {
                    Object.keys(trainInfo).length != 0 ?
                        <Layout>
                            <Header style={{ height: '250px', backgroundColor: '#fff' }}>
                                <div className="order-trian-info">
                                    <Card
                                        title="列车信息"
                                        headStyle={{ backgroundColor: '#3B99FC' }}
                                        bodyStyle={{ backgroundColor: '#EEF1F8' }}
                                    >
                                        <p className="order-train-info-p">
                                            <strong className="order-train-info-mr5">{trainInfo.startDate}</strong>
                                            <strong className="order-train-info-ml5">G2121</strong>
                                            次
                                            <strong className="order-train-info-ml5">成都东</strong>
                                            站
                                            <strong className="order-train-info-s">（17:35开）——北京</strong>
                                            站（19:35到）
                                        </p>
                                        <p>
                                            <span>豪华（
                                                <span style={{ color: '#fb7403' }}>￥500</span>
                                                ）6张
                                            </span>
                                            <span style={{ marginLeft: '50px' }}>普通（
                                                <span style={{ color: '#fb7403' }}>￥500</span>
                                                ）6张
                                            </span>
                                            <span style={{ marginLeft: '50px' }}>经济（
                                                <span style={{ color: '#fb7403' }}>￥500</span>
                                                ）6张
                                            </span>
                                        </p>
                                    </Card>
                                </div>
                            </Header>
                            <Content style={{ height: '450px', backgroundColor: '#fff' }}>
                                <div className="order-passenger-info">
                                    <Card
                                        title="乘客信息"
                                        headStyle={{ backgroundColor: '#3B99FC' }}
                                        bodyStyle={{ backgroundColor: '#FFFFFF' }}
                                    >
                                        <div>
                                            <span>
                                                <IconFont type="anticonchengcheren" />
                                                <span style={{ marginLeft: '10px' }}>乘车人</span>
                                            </span>
                                        </div>
                                        <div>
                                            <Checkbox.Group style={{ width: '100%', marginLeft: '25px' }} onChange={this.checkPassenger} >
                                                <Row>
                                                    <Col span={2}>
                                                        <Checkbox onChange={this.handleCheck} value={1}>张三</Checkbox>
                                                    </Col>
                                                    <Col span={2}>
                                                        <Checkbox onChange={this.handleCheck} value={2}>李四</Checkbox>
                                                    </Col>
                                                </Row>
                                            </Checkbox.Group>
                                        </div>
                                        <Divider dashed />
                                        <Table
                                            columns={columns}
                                            dataSource={dataSource}
                                            bordered
                                            rowKey="key"
                                            pagination={false}
                                        />
                                    </Card>
                                </div>
                            </Content>
                        </Layout> : null
                }
                <Footer style={{ backgroundColor: '#fff' }}>
                    <Button onClick={this.showOrderInfo} type="primary" size="large" shape="round" style={{ marginLeft: '600px' }}>提交订单</Button>
                </Footer>
                {/* 确认订单信息对话框 */}
                <div>
                    <Modal
                        title="核对一下信息"
                        visible={visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                        cancelText="返回修改"
                        destroyOnClose
                        width='800px'
                    >
                        <p className="order-train-info-p">
                            <strong className="order-train-info-mr5">2021-07-04</strong>
                            <strong className="order-train-info-ml5">G2121</strong>
                            次
                            <strong className="order-train-info-ml5">成都东</strong>
                            站
                            <strong className="order-train-info-s">（17:35开）——北京</strong>
                            站（19:35到）
                        </p>
                        <Table
                            columns={columnsInfo}
                            dataSource={dataSource}
                            bordered
                            rowKey="key"
                            pagination={false}
                        />
                    </Modal>
                </div>
            </div>
        )
    }
}
