import React, { Component } from 'react'
import {
    Input, Card, Table,
    Drawer, Form, Button, Col, Row, Space, message, Radio, Popconfirm
} from 'antd';
import {
    PlusOutlined
} from '@ant-design/icons';
import LinkButton from '../../../components/LinkButton';

export default class PlatForm extends Component {
    myForm = React.createRef()
    state = { visible: 0 };

    // 打开添加抽屉
    showAddUser = () => {
        this.setState({ visible: 1 })
    }

    // 关闭抽屉
    onClose = () => {
        this.setState({ visible: 0 })
        this.myForm.current.resetFields()
    }

    // 添加用户
    addUser = () => {
        message.success('添加成功')
        this.setState({ visible: 0 })
        this.myForm.current.resetFields()
    }

    // 打开编辑的抽屉
    showUpdateUser = () => {
        this.setState({ visible: 2 })
    }

    updateUser = () => {
        message.success('编辑成功')
        this.setState({ visible: 0 })
        this.myForm.current.resetFields()
    }

    handleDelete = (key) => {
        const dataSource = [...this.state.dataSource];
        const newDataSource = dataSource.filter((item) => {
            if (item.key !== key) {
                if (item.key > key) {
                    --item.key
                    --item.number
                }
                return item
            }
        })
        if (newDataSource.length === 0) this.setState({ count: 1 })
        this.setState({
            dataSource: newDataSource,
        });
    }

    render() {
        const { visible } = this.state
        const columns = [
            {
                title: '姓名',
                dataIndex: 'name',
                key: '1',
                align: 'center',
            },
            {
                title: '身份证',
                dataIndex: 'idCard',
                key: '2',
                align: 'center',
            },
            {
                title: '手机号码',
                dataIndex: 'phone',
                key: '3',
                align: 'center',
            },
            {
                title: '操作',
                key: '4',
                render: () => (
                    <Space size="middle">
                        <LinkButton onClick={this.showUpdateUser}>编辑</LinkButton>
                        <Popconfirm title="确定删除吗" onConfirm={() => this.handleDelete(record.key)}>
                            <LinkButton >删除</LinkButton>
                        </Popconfirm>
                    </Space>
                ),
                align: 'center'
            },
        ];

        const dataSource = [
            {
                key: '1',
                phone: '123456',
                name: '李四',
                state: '启用',
            },
        ]
        return (
            <div>
                <div>
                    <Card
                        extra={
                            <Button icon={<PlusOutlined />} type="primary" onClick={this.showAddUser}>新增</Button>
                        }
                    >
                        <Table
                            columns={columns}
                            dataSource={dataSource}
                            bordered
                            rowKey="key"
                            pagination={false}
                        />
                    </Card>
                </div>
                {/* 新增的抽屉 */}
                <div>
                    <Drawer
                        title="新增用户"
                        width={500}
                        onClose={this.onClose}
                        visible={visible === 1}
                        bodyStyle={{ paddingBottom: 80 }}
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.addUser}
                            ref={this.myForm}
                        >
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="phone"
                                        label="手机号码"
                                        rules={[{ required: true, message: '请输入手机号码!' }]}
                                        required="true"
                                    >
                                        <Input autoComplete="off" placeholder="请输入手机号码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="name"
                                        label="姓名"
                                        rules={[{ required: true, message: '请输入姓名!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入姓名" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="password"
                                        label="密码"
                                        rules={[{ required: true, message: '请输入密码!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入密码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="state"
                                        label="状态"
                                        rules={[{ required: true, message: '请选择状态！' }]}
                                    >
                                        <Radio.Group name="state" >
                                            <Radio value={1}>启用</Radio>
                                            <Radio value={2}>禁用</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Drawer>
                </div>
                {/* 编辑的抽屉 */}
                <div>
                    <Drawer
                        title="编辑用户"
                        width={500}
                        onClose={this.onClose}
                        visible={visible === 2}
                        bodyStyle={{ paddingBottom: 80 }}
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.updateUser}
                            ref={this.myForm}
                        >
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="phone"
                                        label="手机号码"
                                        rules={[{ required: true, message: '请输入手机号码!' }]}
                                        required="true"
                                    >
                                        <Input autoComplete="off" placeholder="请输入手机号码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="name"
                                        label="姓名"
                                        rules={[{ required: true, message: '请输入姓名!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入姓名" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="password"
                                        label="密码"
                                        rules={[{ required: true, message: '请输入密码!' }]}
                                    >
                                        <Input autoComplete="off" placeholder="请输入密码" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="state"
                                        label="状态"
                                        rules={[{ required: true, message: '请选择状态！' }]}
                                    >
                                        <Radio.Group name="state" >
                                            <Radio value={1}>启用</Radio>
                                            <Radio value={2}>禁用</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.onClose} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Drawer>
                </div>
            </div>
        )
    }
}
