import React, { useContext, useState, useEffect, useRef } from 'react';
import { Card, Button, Table, Modal, Space, Form, Input, Col, Row, Radio    , Popconfirm,Divider } from 'antd'
import {
    PlusOutlined
} from '@ant-design/icons';

import LinkButton from '../../../components/LinkButton';

const EditableContext = React.createContext(null);
const EditableRow = ({ index, ...props }) => {
    const [form] = Form.useForm();
    return (
        <Form form={form} component={false}>
            <EditableContext.Provider value={form}>
                <tr {...props} />
            </EditableContext.Provider>
        </Form>
    );
};

const EditableCell = ({
    title,
    editable,
    children,
    dataIndex,
    record,
    handleSave,
    ...restProps
}) => {
    const [editing, setEditing] = useState(false);
    const inputRef = useRef(null);
    const form = useContext(EditableContext);
    useEffect(() => {
        if (editing) {
            inputRef.current.focus();
        }
    }, [editing]);

    const toggleEdit = () => {
        setEditing(!editing);
        form.setFieldsValue({
            [dataIndex]: record[dataIndex],
        });
    };

    const save = async () => {
        try {
            const values = await form.validateFields();
            toggleEdit();
            handleSave({ ...record, ...values });
        } catch (errInfo) {
            console.log('Save failed:', errInfo);
        }
    };

    let childNode = children;

    if (editable) {
        childNode = editing ? (
            <Form.Item
                style={{
                    margin: 0,
                }}
                name={dataIndex}
                rules={[
                    {
                        required: true,
                        message: `${title}是必填的`,
                    },
                ]}
            >
                <Input ref={inputRef} onPressEnter={save} onBlur={save} />
            </Form.Item>
        ) : (
            <div
                className="editable-cell-value-wrap"
                style={{
                    paddingRight: 24,
                }}
                onClick={toggleEdit}
            >
                {children}
            </div>
        );
    }

    return <td {...restProps}>{childNode}</td>;
};

export default class CarriageInfo extends React.Component {
    constructor(props) {
        super(props);
        this.columns = [
            {
                title: '车厢序号',
                dataIndex: 'carriageOrder',
                key: '2',
                align: 'center',
            },
            {
                title: '车厢编号',
                dataIndex: 'carriageNumber',
                key: '3',
                align: 'center',
                editable: true,
            },
            {
                title: '类别',
                dataIndex: 'type',
                key: '4',
                align: 'center',
                editable: true,
            },
            {
                title: '座位数',
                dataIndex: 'seatNumber',
                key: '5',
                align: 'center',
                editable: true,
            },
            {
                title: '价格基数',
                dataIndex: 'priceBase',
                key: '6',
                align: 'center',
                editable: true,
            },
            {
                title: '操作',
                align: 'center',
                render: (_, record) =>
                    this.state.dataSource.length >= 1 ? (
                        <div>
                            <Popconfirm title="确定删除吗" onConfirm={() => this.handleDelete(record.key)}>
                                <LinkButton >删除</LinkButton>
                            </Popconfirm>
                        </div>
                    ) : null,
            },
        ];
        this.state = {
            dataSource: [
                // {
                //     key: '1',
                //     carriageOrder: 1,
                //     carriageNumber: '001',
                //     type: '动车',
                //     seatNumber: 40,
                //     priceBase: 5,
                // },
                // {
                //     key: '2',
                //     carriageOrder: 2,
                //     carriageNumber: '001',
                //     type: '动车',
                //     seatNumber: 40,
                //     priceBase: 5,
                // },
            ],
            count: 1,
            showStatus:0,
        };
    }


    handleDelete = (key) => {
        const dataSource = [...this.state.dataSource];
        const newDataSource = dataSource.filter((item) => {
            if (item.key !== key) {
                if (item.key > key) {
                    --item.key
                    --item.carriageOrder
                }
                return item
            }
        })
        if (newDataSource.length === 0) this.setState({ count: 1 })
        this.setState({
            dataSource: newDataSource,
        });
    };
    handleAdd = () => {
        const { dataSource } = this.state;
        const newData = {
            key: dataSource.length + 1,
            carriageOrder: dataSource.length + 1,
            carriageNumber: '002',
            type: '豪华',
            seatNumber: 40,
            priceBase: 4,
        };
        this.setState({
            dataSource: [...dataSource, newData],
            count: dataSource.length + 1,
        });
    };
    handleSave = (row) => {
        const newData = [...this.state.dataSource];
        const index = newData.findIndex((item) => row.key === item.key);
        const item = newData[index];
        newData.splice(index, 1, { ...item, ...row });
        this.setState({
            dataSource: newData,
        });
    };

    render() {
        const { dataSource,showStatus } = this.state;
        const components = {
            body: {
                row: EditableRow,
                cell: EditableCell,
            },
        };
        const columns = this.columns.map((col) => {
            if (!col.editable) {
                return col;
            }

            return {
                ...col,
                onCell: (record) => ({
                    record,
                    editable: col.editable,
                    dataIndex: col.dataIndex,
                    title: col.title,
                    handleSave: this.handleSave,
                }),
            };
        });
        return (
            <div>
                <div>
                    <Card
                        title={<Divider orientation="left" plain>
                            <Button shape='round'>车厢信息</Button>
                        </Divider>}
                        size='small'
                        extra={<Button onClick={this.handleAdd} icon={<PlusOutlined />} type="primary" style={{ marginRight: '100px' }}></Button>}
                    >
                        <Table
                            components={components}
                            rowClassName={() => 'editable-row'}
                            bordered
                            dataSource={dataSource}
                            columns={columns}
                            pagination={false}
                        />
                    </Card>
                </div>
                {/* 添加车厢信息的模态窗口 */}
                <div>
                    <Modal
                        title="添加车厢信息"
                        visible={showStatus === 1}
                        onOk={this.addCarriage}
                        onCancel={this.handleCancel}
                        footer={[]}
                        width='800px'
                        style={{ top: 10 }}
                        destroyOnClose='true'
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.onFinish}
                        >
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="carriageNumber"
                                        label="编号"
                                        rules={[{ required: true, message: '请输入编号' }]}
                                        required="true"
                                    >
                                        <Input placeholder="请输入编号" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="priceBase"
                                        label="价格基数"
                                        rules={[{ required: true, message: '请输入价格基数' }]}
                                    >
                                        <Input placeholder="请输入价格基数" />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={4}>
                                    <Form.Item
                                        name="sizeLeft"
                                        label="规格"
                                        rules={[{ required: true, message: '请输入规格' }]}
                                    >
                                        <Input ref={(input) => { this.leftInput = input }} onBlur={this.handleBlur} placeholder="左" />
                                    </Form.Item>
                                </Col>
                                <Col span={4}>
                                    <Form.Item
                                        name="sizeRight"
                                        label=" "
                                        rules={[{ required: true, message: '请输入规格' }]}
                                    >
                                        <Input ref={(input) => { this.rightInput = input }} onBlur={this.handleBlur} placeholder="右" />
                                    </Form.Item>
                                </Col>
                                <Col span={4}>
                                    <Form.Item
                                        name="sizeRow"
                                        label=" "
                                        rules={[{ required: true, message: '请输入规格' }]}
                                    >
                                        <Input ref={(input) => { this.rowInput = input }} placeholder="行" onBlur={this.handleBlur} />
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="seatNumber"
                                        label="座位数"
                                        rules={[{ required: false }]}
                                    >
                                        <span>{seatCount}</span>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="level"
                                        label="级别"
                                        rules={[{ required: true }]}
                                    >
                                        <Radio.Group>
                                            <Radio value={1}>豪华</Radio>
                                            <Radio value={2}>普通</Radio>
                                            <Radio value={3}>经济</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={12}>
                                    <Form.Item
                                        name="adapter"
                                        label="适配车型"
                                        rules={[{ required: true }]}
                                    >
                                        <Radio.Group name="radiogroup" >
                                            <Radio value={1}>动车</Radio>
                                            <Radio value={2}>特快</Radio>
                                            <Radio value={3}>动车、特快</Radio>
                                        </Radio.Group>
                                    </Form.Item>
                                </Col>
                            </Row>
                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.handleCancel} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Modal>
                </div>
            </div>
        );
    }
}
