import { Card, Divider, Table, Checkbox, Row, Col, Select, Popconfirm, Button, Layout, Modal, message } from 'antd'
import React, { Component } from 'react'
import UserHead from '../../../components/UserHead';
import { createFromIconfontCN } from '@ant-design/icons';
import './OrderTicket.css'
import { getPassengerList } from '../../../api/user/user';
import storageUtils from '../../../utils/storageUtils'

const IconFont = createFromIconfontCN({
    scriptUrl: '//at.alicdn.com/t/font_2779508_d3ob7o1ykpv.js',
})

const { Option } = Select
const { Header, Content, Footer } = Layout
export default class OrderTicket extends Component {

    state = {
        order: 1,
        visible: false,
        trainInfo: {},
        passengerList: [],
        dataSource: []
    }

    // 选中乘客
    checkPassenger = (checkValus) => {
        // const { order, dataSource } = this.state
        // const newData = {
        //     key: order,
        //     order: order,
        //     level: 1,
        //     name: '张三',
        //     idCard: '56565',
        //     phone: '18222',
        // }
        // this.setState({
        //     dataSource: [...dataSource, newData],
        //     order: order + 1
        // })

    }
    // 删除乘客
    deletePassenger = (key) => {
        let { order } = this.state
        const dataSource = [...this.state.dataSource]
        const newDataSource = dataSource.filter((item) => {
            if (item.id !== key) {
                if (item.id > key) {
                    // --item.id
                    --item.order
                }
                order--
                return item
            }
        })
        if (newDataSource.length === 0) this.setState({ order: 1 })
        this.setState({
            dataSource: newDataSource,
            checked: false,
            order
        })
    }

    // 处理选中乘客
    handleCheck = (event) => {
        const { dataSource, passengerList, order } = this.state
        const { checked, value } = event.target
        if (checked) {
            let newData = passengerList.find((item) => {
                if (item.id === value) return item
            })
            const newPassenger = { ...newData, order }
            this.setState({
                dataSource: [...dataSource, newPassenger],
                order: order + 1,
                checked: true
            })
        } else {
            this.deletePassenger(value)
        }
    }

    // 处理对话框完成
    handleOk = () => {
        this.setState({ visible: false })
        this.props.history.push({ pathname: '/user/orderInfo' })
    }

    // 关闭对话框
    handleCancel = () => {
        this.setState({ visible: false })
    }

    // 打开订单详情确认对话框
    showOrderInfo = () => {
        this.setState({ visible: true })
    }

    async componentDidMount() {
        // 获取乘车人信息
        const result = await getPassengerList(storageUtils.getUser().id)
        if (result.code === 1) {
            this.setState({
                passengerList: result.data,
                trainInfo: this.props.location.state
            })
        } else {
            message.error(result.msg)
        }

    }

    render() {
        const { passengerList, visible, trainInfo, dataSource } = this.state
        const columns = [
            {
                title: '序号',
                dataIndex: 'order',
                key: '1',
                align: 'center',
            },
            {
                title: '席别',
                dataIndex: 'level',
                key: '2',
                align: 'center',
                render: () => (
                    <Select style={{ width: 100 }} defaultValue={1} >
                        <Option value={1}>豪华</Option>
                        <Option value={2}>普通</Option>
                    </Select>
                ),

            },
            {
                title: '姓名',
                dataIndex: 'passengerName',
                key: '3',
                align: 'center',
            },
            {
                title: '证件号码',
                key: '4',
                dataIndex: 'passengerCard',
                align: 'center',
            },
            {
                title: '手机号码',
                key: '5',
                dataIndex: 'passengerPhone',
                align: 'center',
            },
            // {
            //     title: '操作',
            //     key: '7',
            //     render: (_, record) =>
            //         this.state.dataSource.length >= 1 ? (
            //             <Popconfirm title="确定删除吗？" onConfirm={() => this.deletePassenger(record.id)}>
            //                 <IconFont type="anticonicon--shanchu" />
            //             </Popconfirm>
            //         ) : null,
            //     align: 'center'
            // },
        ];

        const columnsInfo = [
            {
                title: '序号',
                dataIndex: 'order',
                key: '1',
                align: 'center',
            },
            {
                title: '席别',
                dataIndex: 'level',
                key: '2',
                align: 'center',
            },
            {
                title: '姓名',
                dataIndex: 'passengerName',
                key: '3',
                align: 'center',
            },
            {
                title: '证件号码',
                key: '4',
                dataIndex: 'passengerCard',
                align: 'center',
            },
            {
                title: '手机号码',
                key: '5',
                dataIndex: 'passengerPhone',
                align: 'center',
            },
        ];

        return (
            <div>
                <UserHead />
                {
                    Object.keys(trainInfo).length !== 0 ?
                        <Layout>
                            <Header style={{ height: '250px', backgroundColor: '#fff' }}>
                                <div className="order-trian-info">
                                    <Card
                                        title="列车信息"
                                        headStyle={{ backgroundColor: '#3B99FC' }}
                                        bodyStyle={{ backgroundColor: '#EEF1F8' }}
                                    >
                                        <p className="order-train-info-p">
                                            <strong className="order-train-info-mr5">{trainInfo.startDate}</strong>
                                            <strong className="order-train-info-ml5">{trainInfo.trainNumber}</strong>
                                            次
                                            <strong className="order-train-info-ml5">{trainInfo.startName}</strong>
                                            站
                                            <strong className="order-train-info-s">（{trainInfo.startTime}开）——{trainInfo.endName}</strong>
                                            站（{trainInfo.endTime}到）
                                        </p>
                                        <p>
                                            {
                                                trainInfo.luxury ?
                                                    <span>豪华（
                                                        <span style={{ color: '#fb7403' }}>￥500</span>
                                                        ）{trainInfo.luxury} 张
                                                    </span> : null
                                            }
                                            {
                                                trainInfo.ordinal ? <span style={{ marginLeft: '50px' }}>普通（
                                                    <span style={{ color: '#fb7403' }}>￥500</span>
                                                    ）{trainInfo.ordinal} 张
                                                </span> : null
                                            }
                                            {
                                                trainInfo.enco ? <span style={{ marginLeft: '50px' }}>经济（
                                                    <span style={{ color: '#fb7403' }}>￥500</span>
                                                    ）{!trainInfo.enco} 张
                                                </span> : null
                                            }
                                        </p>
                                    </Card>
                                </div>
                            </Header>
                            <Content style={{ height: '450px', backgroundColor: '#fff' }}>
                                <div className="order-passenger-info">
                                    <Card
                                        title="乘客信息"
                                        headStyle={{ backgroundColor: '#3B99FC' }}
                                        bodyStyle={{ backgroundColor: '#FFFFFF' }}
                                    >
                                        <div>
                                            <span>
                                                <IconFont type="anticonchengcheren" />
                                                <span style={{ marginLeft: '10px' }}>乘车人</span>
                                            </span>
                                        </div>
                                        <div>
                                            <Checkbox.Group style={{ width: '100%', marginLeft: '25px' }} onChange={this.checkPassenger} >
                                                <Row>
                                                    {
                                                        passengerList.map((item) => {
                                                            return (
                                                                <Col span={2}>
                                                                    <Checkbox onChange={this.handleCheck} key={item.id} value={item.id}>{item.passengerName}</Checkbox>
                                                                </Col>
                                                            )
                                                        })
                                                    }
                                                </Row>
                                            </Checkbox.Group>
                                        </div>
                                        <Divider dashed />
                                        <Table
                                            columns={columns}
                                            dataSource={dataSource}
                                            bordered
                                            rowKey="id"
                                            pagination={false}
                                        />
                                    </Card>
                                </div>
                            </Content>
                        </Layout> : null
                }
                <Footer style={{ backgroundColor: '#fff' }}>
                    <Button onClick={this.showOrderInfo} type="primary" size="large" shape="round" style={{ marginLeft: '600px' }}>提交订单</Button>
                </Footer>
                {/* 确认订单信息对话框 */}
                <div>
                    <Modal
                        title="核对一下信息"
                        visible={visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                        cancelText="返回修改"
                        destroyOnClose
                        width='800px'
                    >
                        <p className="order-train-info-p">
                            <strong className="order-train-info-mr5">2021-07-04</strong>
                            <strong className="order-train-info-ml5">G2121</strong>
                            次
                            <strong className="order-train-info-ml5">成都东</strong>
                            站
                            <strong className="order-train-info-s">（17:35开）——北京</strong>
                            站（19:35到）
                        </p>
                        <Table
                            columns={columnsInfo}
                            dataSource={dataSource}
                            bordered
                            rowKey="id"
                            pagination={false}
                        />
                    </Modal>
                </div>
            </div>
        )
    }
}
