import React, { Component } from 'react';
import { Card, Button, Table, Modal, Space, Form, Input, Select, Popconfirm, Divider, message } from 'antd'
import {
    PlusOutlined
} from '@ant-design/icons';
import LinkButton from '../../../components/LinkButton';


const { Option } = Select
export default class SiteInfo extends Component {

    state = {
        showStatus: 0,
        count: 0,
    }

    addSite = () => {
        this.setState({ showStatus: 1 })
    }

    // 处理点击取消
    handleCancel = () => {
        this.setState({ showStatus: 0 })
    }

    // 表单提交
    onFinish = (values) => {
        message.success("添加成功")
        this.setState({ showStatus: 0 })
    }

    render() {

        const { showStatus } = this.state
        const columns = [
            {
                title: '序号',
                dataIndex: 'number',
                key: '1',
                align: 'center',
            },
            {
                title: '到达城市',
                dataIndex: 'end',
                key: '2',
                align: 'center',
            },
            {
                title: '用时（分）',
                dataIndex: 'useTime',
                key: '3',
                align: 'center',
            }
            ,
            {
                title: '操作',
                key: '4',
                align: 'center',
                render: (_, record) =>
                    this.state.data.length >= 1 ? (
                        <div>
                            <Popconfirm title="确定删除吗" onConfirm={() => this.handleDelete(record.key)}>
                                <LinkButton >删除</LinkButton>
                            </Popconfirm>
                        </div>
                    ) : null,
            }
        ];

        const data = [
            {
                key: '1',
                number: 1,
                end: '成都',
                useTime: 8,
            },
            {
                key: '2',
                number: 1,
                end: '北京',
                useTime: 7,
            },
        ]

        return (
            <div>
                <div>
                    <Card
                        title={<Divider orientation="left" plain>
                            <Button shape='round'>站点信息</Button>
                        </Divider>}
                        size='small'
                        extra={<Button onClick={this.addSite} icon={<PlusOutlined />} type="primary" style={{ marginRight: '20px' }}></Button>}
                    >
                        <Table
                            columns={columns}
                            dataSource={data}
                            bordered
                            rowKey="key"
                            pagination={false}
                        />
                    </Card>
                </div>
                {/* 添加车厢信息的模态窗口 */}
                <div>
                    <Modal
                        title="添加车厢信息"
                        visible={showStatus === 1}
                        onOk={this.handleAdd}
                        onCancel={this.handleCancel}
                        footer={[]}
                        width='800px'
                        style={{ top: 100 }}
                        destroyOnClose='true'
                    >
                        <Form
                            layout="vertical"
                            onFinish={this.onFinish}
                        >
                            <Form.Item
                                name="site"
                                label="站点"
                                rules={[{ required: true, message: '请选择站点' }]}

                            >
                                <Select>
                                    <Option value="站点1">站点1</Option>
                                    <Option value="站点2">站点2</Option>
                                </Select>
                            </Form.Item>

                            <div
                                style={{
                                    textAlign: 'right',
                                    marginTop: '50px'
                                }}
                            >
                                <Space size="large">
                                    <Button onClick={this.handleCancel} style={{ marginRight: 8 }}>
                                        取消
                                    </Button>
                                    <Button type="primary" htmlType="submit">
                                        确认
                                    </Button>
                                </Space>

                            </div>
                        </Form>
                    </Modal>
                </div>
            </div>
        )
    }
}
