import React, { Component } from 'react'
import { Layout, message } from 'antd'
import { Link } from 'react-router-dom'
import storageUtils from '../../utils/storageUtils'
import LinkButton from '../LinkButton'
import { userLogout } from '../../api/user/user'

const { Header } = Layout
export default class UserHead extends Component {

    logout = async () => {
        const user = storageUtils.getUser()
        storageUtils.removeUser()
        storageUtils.removeUserToken()
        const result = await userLogout(user.id)
        if (result.code === 1) {
            console.log(this)
            // this.props.history.push({ pathname: '/user/login' })
        } else {
            message.error(result.msg)
        }
    }

    render() {
        const user = storageUtils.getUser()
        return (
            <div>
                <Header className="head">订票系统
                    <span style={{ marginLeft: '800px', fontSize: '15px' }}>
                        {
                            !user ? <Link to="/user/login" style={{ marginLeft: '20px' }}>登录</Link> : <span>{user.phone}<LinkButton onClick={this.logout} style={{ marginLeft: '20px' }}>退出</LinkButton></span>
                        }
                    </span>
                </Header>
            </div>
        )
    }
}
