import store from 'store'
const USER_KEY = 'user_key'
const TOKEN_KEY = 'token_key'
export default {
    // 保存user
    saveUser(user) {
        store.set(USER_KEY, user)
    },

    // 读取user
    getUser() {
        return store.get(USER_KEY) || {}
    },

    // 删除user
    removeUser() {
        store.remove(USER_KEY)
    },

    // 保存token
    saveToken(token) {
        store.set(TOKEN_KEY, token)
    },
    getToken() {
        store.get(TOKEN_KEY) || ''
    },
    removeToken() {
        store.remove(TOKEN_KEY)
    }

}
