    },

    // 读取admin
    getAdmin() {
        return store.get(ADMIN_KEY) || {}
    },

    // 删除user
    removeAdmin() {
        store.remove(ADMIN_KEY)
    },

    // 保存token
    saveAdminToken(token) {
        store.set(ADMIN_TOKEN_KEY, token)
    },
    getAdminToken() {
        return store.get(ADMIN_TOKEN_KEY)
    },
    removeAdminToken() {
        store.remove(ADMIN_TOKEN_KEY)
    }

}
