import React, { Component } from 'react'
import moment from 'moment';
import {
    Input, Table, Card, Form, Button, Col, Row, Select, DatePicker, message
} from 'antd'
import { Link } from 'react-router-dom'
import storageUtils from '../../../utils/storageUtils'
import { getMyOrderList } from '../../../api/user/user';

const { Option } = Select
const { RangePicker } = DatePicker
export default class MyOrder extends Component {
    state = {
        pageSize: 5,
        dataSource: []
    }
    myForm = React.createRef()
    // 清空表单
    onReset = () => {
        this.myForm.current.resetFields()
    }

    // 设置不可选用的时间
    disabledDate = (current) => {
        return current && current < moment().subtract(1, "days")
    }

    async componentDidMount() {
        const user = storageUtils.getUser()
        const { pageSize } = this.state
        const result = await getMyOrderList(user.id, 1, pageSize)
        if (result.code === 1) {
            console.log(result.data)
            this.setState({ dataSource: result.data })
        } else {
            message.error(result.msg)
        }
    }

    render() {
        const { dataSource } = this.state
        const user = storageUtils.getUser()
        const columns = [
            {
                title: '车次',
                key: '1',
                dataIndex: 'trainNumber',
                align: 'center',
            },
            {
                title: '始发地',
                dataIndex: 'startName',
                key: '2',
                align: 'center',
            },
            {
                title: '终点站',
                dataIndex: 'endName',
                key: '3',
                align: 'center',
            },
            {
                title: '出发日期',
                dataIndex: 'startDate',
                key: '4',
                align: 'center',
            },
            {
                title: '出发时间',
                dataIndex: 'startTime',
                key: '5',
                align: 'center',
            },

            {
                title: '级别',
                key: '6',
                dataIndex: 'levelCar',
                align: 'center',
                render: (record) => {
                    console.log(record)
                    return record === 1 ? '豪华' : (record === 2 ? '普通' : '经济')
                }
            },
            {
                title: '车厢',
                key: '7',
                dataIndex: 'carriageOrder',
                align: 'center',
            },
            {
                title: '座位号',
                key: '8',
                dataIndex: 'seatNumber',
                align: 'center',
            },
            {
                title: '乘车人',
                key: '9',
                dataIndex: 'passengerName',
                align: 'center',
            },
            {
                title: '身份证',
                key: '10',
                dataIndex: 'passengerCard',
                align: 'center',
            },
            {
                title: '订单状态',
                key: '11',
                dataIndex: 'orderStatus',
                align: 'center',
                render: (record) => {
                    return record === 0 ? '未支付' : (record === 1 ? '已支付' : '已取消')
                }
            },
        ];

        // const dataSource = [
        //     {
        //         key: '1',
        //         startStation: '成都',
        //         endStation: '北京',
        //         startDate: '2021.01.01',
        //         trainsNumber: 'G2154',
        //         seatType: '豪华',
        //         carriageNumber: '1',
        //         seatNumber: 'A12',
        //         passenger: '张三',
        //         idCard: '5108212121212',
        //         orderState: '已支付',
        //     },
        // ]

        return (
            <div>
                <div>
                    {
                        Object.keys(user).length === 0 ?
                            <span>请先<Link to="/user/login">登录</Link></span> :
                            <Card>
                                <div>
                                    <Form
                                        ref={this.myForm}
                                        layout="inline"
                                        onFinish={this.onFinish}
                                    >
                                        <Row gutter={16}>
                                            <Col span={12}>
                                                <Form.Item
                                                    name="出发时间"
                                                >
                                                    <RangePicker disabledDate={this.disabledDate} />
                                                </Form.Item>
                                            </Col>
                                            <Col span={6}>
                                                <Form.Item
                                                    name="type"
                                                >
                                                    <Select placeholder="座位类别">
                                                        <Option value={1}>豪华</Option>
                                                        <Option value={2}>经济</Option>
                                                        <Option value={3}>普通</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>
                                            <Col span={6}>
                                                <Form.Item
                                                    name="orderState"
                                                >
                                                    <Select placeholder="订单状态">
                                                        <Option value={1}>已支付</Option>
                                                        <Option value={2}>未支付</Option>
                                                        <Option value={3}>超时取消</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>
                                        </Row>

                                        <Col span={0.3}
                                            style={{ marginRight: '20px' }}
                                        >
                                            <Form.Item>
                                                <Button type="primary" shape="round" >查询</Button>
                                            </Form.Item>
                                        </Col>
                                        <Col span={1}
                                        >
                                            <Form.Item>
                                                <Button shape="round" htmlType="button" onClick={this.onReset}>重置</Button>
                                            </Form.Item>
                                        </Col>
                                    </Form>
                                </div>
                                <div style={{ marginTop: '20px' }}>
                                    <Table
                                        columns={columns}
                                        dataSource={dataSource}
                                        bordered
                                        rowKey="key"
                                    />
                                </div>
                            </Card>
                    }
                </div>
            </div>
        )
    }
}
